// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IUSDC is IERC20 {
  function deposit() external payable;
}

contract StackNDCA {

    // address usdcSmartContractAddress=0x07865c6E87B9F70255377e024ace6630C1Eaa37F;
    address usdcSmartContractAddress=0x12494912CBdEA11277f48ACd5445c3A2770e2c52;

    struct userAccount {
        uint256 usdcAmount;
        uint256 usdcDCA;
        uint256 ethAmount;
    }

    mapping (address => userAccount) public userAccounts;

    function myUsdcBalance() public view returns (uint256) {
        return userAccounts[msg.sender].usdcAmount;
    }

    function depositUsdc(uint256 amountIn) public {
        IUSDC(usdcSmartContractAddress).transferFrom(msg.sender, address(this), amountIn);
        userAccounts[msg.sender].usdcAmount += amountIn;

    }

    function widthdrawUsdc(uint256 amountOu) public {
        userAccounts[msg.sender].usdcAmount -= amountOu;
        IUSDC(usdcSmartContractAddress).transfer(msg.sender, amountOu);
    }

}
